<!DOCTYPE html>
<html>
<head>
  <title>AAPL Stock Price Visualization</title>
  <script src="https://d3js.org/d3.v7.min.js"></script>
  <style>
    body { font-family: sans-serif; }
    .bar { fill: steelblue; }
    .bar-label { fill: white; text-anchor: middle; font-size: 10px; }
    .axis path, .axis line { fill: none; stroke: #000; shape-rendering: crispEdges; }
    .axis text { font-size: 11px; }
    .chart-title { font-size: 16px; font-weight: bold; text-anchor: middle; }
  </style>
</head>
<body>
  <div id="chart"></div>
  <script type="module">
    const margin = {top: 40, right: 30, bottom: 40, left: 60};
    const width = 800 - margin.left - margin.right;
    const height = 600 - margin.top - margin.bottom;

    const svg = d3.select("#chart")
      .append("svg")
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)
      .append("g")
        .attr("transform", `translate(${margin.left},${margin.top})`);

    d3.csv("data.csv", d3.autoType).then(data => {
      // Parse dates
      const parseDate = d3.timeParse("%Y-%m-%d");
      data.forEach(d => {
        d.date = parseDate(d.date);
      });

      // Scales
      const x = d3.scaleTime()
        .domain(d3.extent(data, d => d.date))
        .range([0, width]);

      const y = d3.scaleLinear()
        .domain([0, d3.max(data, d => d.price)])
        .nice()
        .range([height, 0]);

      // Axes
      const xAxis = d3.axisBottom(x);
      const yAxis = d3.axisLeft(y);

      svg.append("g")
        .attr("transform", `translate(0,${height})`)
        .attr("class", "axis")
        .call(xAxis);

      svg.append("g")
        .attr("class", "axis")
        .call(yAxis);

      // Bars
      svg.selectAll(".bar")
        .data(data)
        .enter().append("rect")
          .attr("class", "bar")
          .attr("x", d => x(d.date))
          .attr("y", d => y(d.price))
          .attr("width", width / data.length - 1)
          .attr("height", d => height - y(d.price));

      // Bar labels
      svg.selectAll(".bar-label")
        .data(data)
        .enter().append("text")
          .attr("class", "bar-label")
          .attr("x", d => x(d.date) + (width / data.length - 1) / 2)
          .attr("y", d => y(d.price) - 5)
          .text(d => d.price);

      // Title
      svg.append("text")
        .attr("class", "chart-title")
        .attr("x", width / 2)
        .attr("y", 0 - (margin.top / 2))
        .text("AAPL Stock Price Over Time");

      // Y-axis label
      svg.append("text")
          .attr("transform", "rotate(-90)")
          .attr("y", 0 - margin.left)
          .attr("x",0 - (height / 2))
          .attr("dy", "1em")
          .style("text-anchor", "middle")
          .text("Price ($)");

      // X-axis label
      svg.append("text")
          .attr("x", width / 2)
          .attr("y", height + margin.bottom - 5)
          .style("text-anchor", "middle")
          .text("Date");


    }).catch(error => {
      console.error("Error loading or processing data:", error);
      svg.append("text")
        .attr("x", width / 2)
        .attr("y", height / 2)
        .attr("text-anchor", "middle")
        .text("Error loading data. Check console.");
    });

  </script>
</body>
</html>
